"INSERT INTO public.board_game (id, title, title_normalized, description, min_players, max_players, min_age, max_age, estimated_play_time, can_play_only_once, is_extension, tutorial_url) VALUES (26, 'EXIT - Bouřlivý let', 'exit - bourlivy let', 'Hráči sú súčasťou posádky osobného lietadla smerujúceho na Barbados. Let zastihne neočakávaná búrka a razom sa ocitnete uprostred pekla plného bleskov. Voda preniká do živlami zmietaného stroja a časti technológie zlyhávajú. Podarí sa tímu opraviť lietadlo, dostať komplikovanú situáciu opäť pod kontrolu a bezpečne pristáť? Nezostáva veľa času a búrka neprestáva... Úroveň Začátečník.', 1, 4, 10, NULL, 120, true, false, NULL);"
