"INSERT INTO public.board_game (id, title, title_normalized, description, min_players, max_players, min_age, max_age, estimated_play_time, can_play_only_once, is_extension, tutorial_url) VALUES (11, 'Komu zvoní tramvaj', 'komu zvoni tramvaj', 'Hráči musia rozhodnúť, na ktorú z dvoch koľají pustiť tramvaj, ktorá zmasakruje všetkých, čo na nej stoja. Jeden hráč je výhybkár a ostatní sa rozdelia na dva tímy, ktoré sa snažia presvedčiť výhybkára, aby ich koľaj zachránil a zabil všetkých na tej druhej. Hra je založená na morálnych dilemách.', 3, 13, 15, NULL, 30, false, false, 'https://www.youtube.com/watch?v=F8GzkeWWkR0');"
"INSERT INTO public.board_game (id, title, title_normalized, description, min_players, max_players, min_age, max_age, estimated_play_time, can_play_only_once, is_extension, tutorial_url) VALUES (12, 'Vlajky', 'vlajky', '', 2, 6, 10, NULL, 20, false, false, 'https://www.youtube.com/watch?v=oINxOoUMYfQ');"
"INSERT INTO public.board_game (id, title, title_normalized, description, min_players, max_players, min_age, max_age, estimated_play_time, can_play_only_once, is_extension, tutorial_url) VALUES (13, 'Toulavé Věže', 'toulave veze', 'Študenti mágie absolvujú záverečné skúšky s cieľom doraziť do Havranieho hradu. Cestovať sa dá pešo alebo na cimburiu túlavých veží. Pokiaľ naviac dokážete uzamknúť iných čarodejov, Vaše elixíry sa magicky naplnia. Hráč, ktorý naplní všetky svoje elixíry, najlepšie ich využije a všetkých svojich čarodejov dopraví do Havranieho hradu zvíťazí.', 2, 6, 7, NULL, 30, false, false, 'https://www.youtube.com/watch?v=4sy4BIMQkok');"
"INSERT INTO public.board_game (id, title, title_normalized, description, min_players, max_players, min_age, max_age, estimated_play_time, can_play_only_once, is_extension, tutorial_url) VALUES (14, 'Arnak', 'arnak', 'Uprostred bujnej džungle, v troskách dávno zaniknutej civilizácie, budete objavovať starobylé artefakty, ktoré však strážia desiví ochrancovia.', 1, 4, 12, NULL, 90, false, false, 'https://www.youtube.com/watch?v=GiERdSi5ngI');"
