"INSERT INTO public.board_game (id, title, title_normalized, description, min_players, max_players, min_age, max_age, estimated_play_time, can_play_only_once, is_extension, tutorial_url) VALUES (18, 'Mars: Teraformace - Hellas & Elysium', 'mars: teraformace - hellas & elysium', 'Miesto pôvodného herného plánu môžete vyskúšať dva nové. Znázorňujú iné časti Marsu, sú na nich vyznačené iné bonusy za umiestnenie doštičiek, iné oblasti pre doštičky oceánov a nové čestné tituly a súťaže.', 1, 5, 12, NULL, 90, false, true, 'https://www.youtube.com/watch?v=ujq_2n1RKfY');"
"INSERT INTO public.board_game (id, title, title_normalized, description, min_players, max_players, min_age, max_age, estimated_play_time, can_play_only_once, is_extension, tutorial_url) VALUES (19, 'Memoir 44', 'memoir 44', 'Memoir 44 je skvelá ľahká simulácia bitiek 2.svetovej vojny pre dvoch hráčov. Podľa scenáru si najprv vytvoria celkom podrobný plán bitky na univerzálnej hexovej podložke s množstvom žetónov typu pevnosti, ostnatý drôt, vrecia s pieskom, rieka, bunker, zelezničná trať, atď. a umiestnia na scenárom predpísané polia aj jednotky. Jednotky sú pechota, delostrelectvo a tanky, pričom každý druh má elitnú vylepšenú verziu.', 2, 2, 8, NULL, 40, false, false, 'https://www.youtube.com/watch?v=8Cil0XBhYPY');"
"INSERT INTO public.board_game (id, title, title_normalized, description, min_players, max_players, min_age, max_age, estimated_play_time, can_play_only_once, is_extension, tutorial_url) VALUES (20, 'Memoir 44 - Pacific Theater', 'memoir 44 - pacific theater', 'Kompletne nová japonská cisárska armáda, nové dieliky terénov. Jednotlivé scénáre vás potom prevedú slávnými stretmi ako je Iwojima či Okinawa.', 2, 2, 10, NULL, 30, false, true, 'https://www.youtube.com/watch?v=26x8kYWIIKw');"
"INSERT INTO public.board_game (id, title, title_normalized, description, min_players, max_players, min_age, max_age, estimated_play_time, can_play_only_once, is_extension, tutorial_url) VALUES (21, 'EXIT - Mrtvý muž v Orient Expressu', 'exit - mrtvy muz v orient expressu', 'Je to deduktívna hra, v ktorej musíte vyriešiť rôzne rébusy a hádanky, aby ste odhalili vraha. Úroveň Specialista.', 1, 4, 12, NULL, 120, true, false, NULL);"
"INSERT INTO public.board_game (id, title, title_normalized, description, min_players, max_players, min_age, max_age, estimated_play_time, can_play_only_once, is_extension, tutorial_url) VALUES (22, 'EXIT - Návrat do opuštěné chaty', 'exit - navrat do opustene chaty', 'sa stretáte s detektívom, ktorý ich požiada, aby išli s ním do chaty v lese. Vy viete, že to je tá opustená chata, z ktorej ste sa snažili uniknúť pred „hostiteľom“ Dr. Arthur Funbeq. Ten teraz utiekol z väzenie a vy sa vraciate na miesto činu,  aby ste pomohli vypátrať vášho väzniteľa. Úroveň Pokročilý.', 1, 4, 12, NULL, 120, true, false, NULL);"
"INSERT INTO public.board_game (id, title, title_normalized, description, min_players, max_players, min_age, max_age, estimated_play_time, can_play_only_once, is_extension, tutorial_url) VALUES (23, 'EXIT - Loupež na Mississippi', 'exit - loupez na mississippi', 'Úroveň Pokročilý.', 1, 4, 12, NULL, 120, true, false, NULL);"
"INSERT INTO public.board_game (id, title, title_normalized, description, min_players, max_players, min_age, max_age, estimated_play_time, can_play_only_once, is_extension, tutorial_url) VALUES (24, 'EXIT - Tajemné muzeum', 'exit - tajemne muzeum', 'Pri vstupe do impozantného múzea nevidíta nikde nikoho. Dokonca aj pokladňa je zatvorená. Otočíte sa na odchod a v tom sa vám pred nosom zabuchnú obrovské dvere. Ste tu zamknutí. Vydesene sa obzeráte okolo seba. Druhý východ je zablokovaný turniketom s čudným symbolom. Ale počkať, nevideli ste tento symbol už niekde? A vtedy to pochopíte. Musíte vyriešiť všetky hádanky v múzeu, aby ste sa dostali von. Ak sa vám to nepodarí, stanú sa aj z vás výstavné kusy... Úroveň Začátečník.', 1, 4, 10, NULL, 120, true, false, NULL);"
"INSERT INTO public.board_game (id, title, title_normalized, description, min_players, max_players, min_age, max_age, estimated_play_time, can_play_only_once, is_extension, tutorial_url) VALUES (27, 'Muzeum Podezřelých', 'muzeum podezrelych', 'Úlohou bude odhaliť, kto z 16 podozrivých ukradol z múzea vzácny artefakt!', 2, 4, 8, NULL, 20, false, false, 'https://www.youtube.com/watch?v=cZpfXlhx-G0');"

