INSERT INTO public.board_game (id, title, title_normalized, description, min_players, max_players, min_age, max_age, estimated_play_time, can_play_only_once, is_extension, is_cooperative, tutorial_url, year_published, created_by, version) VALUES (19, 'Memoir 44', 'memoir 44', 'Memoir 44 je skvelá ľahká simulácia bitiek 2.svetovej vojny pre dvoch hráčov. Podľa scenáru si najprv vytvoria celkom podrobný plán bitky na univerzálnej hexovej podložke s množstvom žetónov typu pevnosti, ostnatý drôt, vrecia s pieskom, rieka, bunker, zelezničná trať, atď. a umiestnia na scenárom predpísané polia aj jednotky. Jednotky sú pechota, delostrelectvo a tanky, pričom každý druh má elitnú vylepšenú verziu.', 2, 2, 8, NULL, 40, false, false, false, 'https://www.youtube.com/watch?v=8Cil0XBhYPY', 2004, 'Flyway', 0);
