INSERT INTO public.board_game (id, title, title_normalized, description, min_players, max_players, min_age, max_age, estimated_play_time, can_play_only_once, is_extension, is_cooperative, tutorial_url, year_published, created_by, version) VALUES (43, 'Drako - Zlobři a Rytíři', 'drako - zlobri a rytiri', 'Úlohou hráča hrajúceho za trojicu rytierov je zabiť dvojicu trollov, alebo prežiť noc. Cieľom jeho protivníka je, zabiť rytierov a ukončiť hru skôr, než nastane úsvit. Dĺžka hry je obmedzená balíkom kariet. Končí v okamžiku, keď sa hráčovi hrajúcemu za trollov minie posledná karta.', 2, 2, 8, NULL, 30, false, false, false, 'https://www.youtube.com/watch?v=fOCNUxSVasw', 2016, 'Flyway', 0);
