INSERT INTO public.board_game (id, title, title_normalized, description, min_players, max_players, min_age, max_age, estimated_play_time, can_play_only_once, is_extension, is_cooperative, tutorial_url, year_published, created_by, version) VALUES (32, 'Adventure Games - Žalář', 'adventure games - zalar', 'Prebudili ste sa vo vlhkom žalári. Na prvý pohľad sa zdá byť nemožné nájsť cestu von na slobodu. Nikto z vás si totiž nepamätá, čo sa vlastne stalo a ako ste sa tam ocitli... Musíte preskúmať miestnosti, kombinovať predmety, hľadať stopy a hovoriť s ľuďmi a možno sa vám podarí uniknúť tak, aby vás nechytili.', 1, 4, 12, NULL, 90, false, false, true, NULL, 2019, 'Flyway', 0);
INSERT INTO public.board_game (id, title, title_normalized, description, min_players, max_players, min_age, max_age, estimated_play_time, can_play_only_once, is_extension, is_cooperative, tutorial_url, year_published, created_by, version) VALUES (35, 'Mistr Slova', 'mistr slova', '', 2, NULL, 8, NULL, 20, false, false, false, NULL, 2017, 'Flyway', 0);
INSERT INTO public.board_game (id, title, title_normalized, description, min_players, max_players, min_age, max_age, estimated_play_time, can_play_only_once, is_extension, is_cooperative, tutorial_url, year_published, created_by, version) VALUES (36, 'Karak: Goblin', 'karak: goblin', 'Využite karty, ktoré máte v štartovacom balíčku hrdinov na nákup lepšieho vybavenia, schopností či odvarov. Aj vďaka nim budete môcť porážať silnejšie príšery a prekonávať čoraz nebezpečnejšie nástrahy. Jednoduchý deckbuildingový princíp vám umožnuje vytvárať silné kombinácie kariet a prebojovať sa k víťazstvu!', 2, 4, 7, NULL, 30, false, false, false, 'https://www.youtube.com/watch?v=OspNcLYpp8k', 2021, 'Flyway', 0);
INSERT INTO public.board_game (id, title, title_normalized, description, min_players, max_players, min_age, max_age, estimated_play_time, can_play_only_once, is_extension, is_cooperative, tutorial_url, year_published, created_by, version) VALUES (37, 'Karak', 'karak', 'Každý z hráčov sa zhostí jedného z hrdinov a vyrazí s ním do labyrintu pod hradom Karak bojovať s príšerami a získavať zbrane a poklady. Hrdina disponuje vždy dvojicou unikátnych schopností a len ten, kto ich dokáže efektívne využiť, môže pomýšľať na víťazstvo. Vďaka víťazstvám v bojoch získavajú hrdinovia rôzne vybavenie, čím posilujú svojho hrdinu. Hra končí v okamžiku, kedy niektorí z hrdinov porazí draka. Víťazom sa potom stáva ten, kto v priebehu hry nazbieral najviac pokladov.', 2, 5, 7, NULL, 45, false, false, false, 'https://www.youtube.com/watch?v=s9j_uKjuLu8', 2017, 'Flyway', 0);
INSERT INTO public.board_game (id, title, title_normalized, description, min_players, max_players, min_age, max_age, estimated_play_time, can_play_only_once, is_extension, is_cooperative, tutorial_url, year_published, created_by, version) VALUES (38, 'Karak 2', 'karak 2', 'Každý z hráčov sa zhostí jedného z hrdinov a vyráža na dobrodružstvo, v ktorom spoznáme nové zákutia fantazijného sveta! Okrem súbojov s monštrami budete budovať vlastné mestečko a získavať pomoc, pre boj s armádou temna! Hra ponúka väčšiu komplexnosť, ako pôvodný Karak.', 2, 5, 10, NULL, 45, false, false, false, 'https://www.youtube.com/watch?v=fCfew5ncKdc', 2021, 'Flyway', 0);
INSERT INTO public.board_game (id, title, title_normalized, description, min_players, max_players, min_age, max_age, estimated_play_time, can_play_only_once, is_extension, is_cooperative, tutorial_url, year_published, created_by, version) VALUES (39, 'Prešibané P', 'presibane p', 'Budete sa snažiť ostatných naviesť na uhádnutie tajného slova. Pri opisovaní a naznačovaní hráčom, môžete používať iba slová, ktoré začínajú na písmeno P. Pripravený papierik prezradí predmet pátrania. Pochopiteľnými pomôckami počínajúcimi písmenom „P popíšte predmet pátrania. Počúvajúci pátrajú po popisovanom predmete. Podľa popisu počúvajúci presne pomenuje predmet pátrania. Priateľovi preto prislúcha pirôžok! Pozbieraním piatich pirôžkov porazíte protivníkov!', 3, 6, 10, NULL, 30, false, false, false, NULL, 2010, 'Flyway', 0);
INSERT INTO public.board_game (id, title, title_normalized, description, min_players, max_players, min_age, max_age, estimated_play_time, can_play_only_once, is_extension, is_cooperative, tutorial_url, year_published, created_by, version) VALUES (40, 'Kotlo Mordo', 'kotlo mordo', 'Ponúka oveľa vyšší level zábavy ako čokoľvek iné. Pri hre Kotlo Mordo plníš úlohy, ktoré máš predpísané na kartičkách. Ak ťa úloha nezaujala, môžeš sa namiesto jej plnenia napiť. Hra ponúka naozaj neuveriteľné množstvo zážitkov s kamarátmi. Ideálna zábava na chatovačky.', 4, NULL, 18, NULL, NULL, false, false, false, NULL, 2021, 'Flyway', 0);
INSERT INTO public.board_game (id, title, title_normalized, description, min_players, max_players, min_age, max_age, estimated_play_time, can_play_only_once, is_extension, is_cooperative, tutorial_url, year_published, created_by, version) VALUES (41, 'Munchkin', 'munchkin', 'Obľúbená satirická fantasy hra, ktorá paroduje Dungeon and Dragons a iné RPG hry. Vašou úlohou je pobiť všetko živé na čo narazíte pri preskúmavaní podzemia, získať poklady, podporovať a vzápätí podrážať súperov a predovšetkým rychlejšie ako ostatní zvyšovať úroveň svojej postavy. Víťazí hráč, ktorému sa ako prvému podarí dosiahnuť desiatu úroveň.', 3, 6, 10, NULL, 120, false, false, false, 'https://www.youtube.com/watch?v=RFJwq-xURgY', 2001, 'Flyway', 0);
INSERT INTO public.board_game (id, title, title_normalized, description, min_players, max_players, min_age, max_age, estimated_play_time, can_play_only_once, is_extension, is_cooperative, tutorial_url, year_published, created_by, version) VALUES (42, 'Drako - Drak a Trpaslíci', 'drako - drak a trpaslici', 'Asymetrická hra pre dvoch hráčov, v ktorej majú obaja rôzne možnosti a tiež odlišný cieľ. Úlohou hráča hrajúceho za trpaslíkov je zabiť draka. Cieľom jeho protivníka je, aby drak boj prežil. Hra končí v okamžiku, kedy hráčovi hrajúcemu za trpaslíkov dojdú karty. Pomocou akcií na kartách hráči pohybujú figúrkami, útočia a bránia sa útokom súpera. Túto hru môžete tiež kombinovať s hrou Drako: Zlobři a rytíři.', 2, 2, 8, NULL, 30, false, false, false, 'https://www.youtube.com/watch?v=IJHhnbkh3Jg', 2016, 'Flyway', 0);
INSERT INTO public.board_game (id, title, title_normalized, description, min_players, max_players, min_age, max_age, estimated_play_time, can_play_only_once, is_extension, is_cooperative, tutorial_url, year_published, created_by, version) VALUES (77, 'Monopoly', 'monopoly', 'Investujte, uzatvárajte obchody, vyberajte nájomné a priveďte ostatných k bankrotu. Aby bola nám, Slovákom, o čosi bližšia, na políčkach nájdete ulice a námestia Bratislavy. Takže okrem zábavy spoznáte zaujímavé a známe miesta našeho hlavného mesta.', 2, 6, 8, NULL, 60, false, false, false, 'https://www.youtube.com/watch?v=dvHqVyxdWYU', 1906, 'Flyway', 0);
INSERT INTO public.board_game (id, title, title_normalized, description, min_players, max_players, min_age, max_age, estimated_play_time, can_play_only_once, is_extension, is_cooperative, tutorial_url, year_published, created_by, version) VALUES (78, 'Obludárium', 'obludarium', 'Hráči v úlohe riaditeľa kočovného obludária prichádzajú do zapadnutej dedinky a pokúšajú sa predviesť neopakovateľnú show. Získavate body za ulovené príšery, splnené úlohy a ušetrené toliare. Víťazí hráč, ktorému sa podarí počas štyroch loveckých sezón zhromaždiť najpôsobivejšie a najviac zarábajúce Obludárium.', 2, 5, 10, NULL, 45, false, false, false, 'https://www.youtube.com/watch?v=6ZB78jKnKyU', 2018, 'Flyway', 0);
INSERT INTO public.board_game (id, title, title_normalized, description, min_players, max_players, min_age, max_age, estimated_play_time, can_play_only_once, is_extension, is_cooperative, tutorial_url, year_published, created_by, version) VALUES (79, '7 Divů světa', '7 divu sveta', 'Po dlhé tri veky sa máte starať a zveľaďovať svoje mesto. Ťažiť suroviny, produkovať tovar, podporovať vedu i obchod a často sa vyplatí aj armáda a tiež postupne postaviť jeden z divov sveta. Keď vám práve chýbajú tie správne prostriedky na stavbu, môžete materiál nakúpiť u susedov. Na konci každého veku si s nimi zmeriate sily, takže sa nevyplatí ich príliš finančne podporovať.', 3, 7, 10, NULL, 30, false, false, false, 'https://www.youtube.com/watch?v=PFScwrXmNLc', 2010, 'Flyway', 0);
INSERT INTO public.board_game (id, title, title_normalized, description, min_players, max_players, min_age, max_age, estimated_play_time, can_play_only_once, is_extension, is_cooperative, tutorial_url, year_published, created_by, version) VALUES (80, 'Alchymisté', 'alchymiste', 'Hráči miešajú rôzne ingrediencie, vyrábajú z nich elixíry, predávajú hrdinom, testujú ich na sebe, alebo študentoch a z výsledkov miešania sa snažia vydedukovať akú esenciu obsahuje tá ktorá ingrediencia. Svoje zistenia potom môžu zverejniť v podobe teórií, ktoré však protihráči môžu potvrdiť, alebo ich môžu vyvrátiť. Za správne teórie získavajú hráči body reputácie, za nesprávne ich strácajú. Body sa dajú získať aj z mocných artefaktov a z grantov, ktoré získavajú za zverejňovanie teórií.', 2, 4, 12, NULL, 120, false, false, false, 'https://www.youtube.com/watch?v=3AWcf40-1gc', 2016, 'Flyway', 0);
INSERT INTO public.board_game (id, title, title_normalized, description, min_players, max_players, min_age, max_age, estimated_play_time, can_play_only_once, is_extension, is_cooperative, tutorial_url, year_published, created_by, version) VALUES (81, 'Desiatka', 'desiatka', 'Kto odpovie správne, získa jeden zo žetónov odpovedí ako víťazný bod. V ďalšom ťahu má hráč možnosť opäť hľadať správnu odpoveď, ale tentokrát riskuje, že príde o body, ktoré v predchádzajúcej odpovedi získal. Istota alebo risk? V hre je 200 okruhov, 2000 otázok a rovnaký počet možných odpovedí. Všetko je to v šikovnom balení zvanom smartbox s počítadlom bodov a žetónmi odpovedí. Hra zabaví celú rodinu a je vhodná na cesty.', 2, 8, 10, NULL, 20, false, false, false, 'https://www.youtube.com/watch?v=QFTbfudEiiA', 2015, 'Flyway', 0);
INSERT INTO public.board_game (id, title, title_normalized, description, min_players, max_players, min_age, max_age, estimated_play_time, can_play_only_once, is_extension, is_cooperative, tutorial_url, year_published, created_by, version) VALUES (82, 'Hvězdokupy', 'hvezdokupy', 'Pozri, vychádza hviezda! Za ňou druhá, tretia a už vidíme skoro celý Veľký voz! Lenže hviezd je zrazu celá kopa a my sme sa v nej úplne stratili. Kto sa najrýchlejšie zorientuje a nájde svoje súhvezdie ako prvý? Hráči sa snažia behom hry umiestňovať na oblohu (hrací plán) hviezdy a utvoriť tak svoje súhvezdie. Ako to ale dopadne, keď sa o to snažia všeci hráči?', 2, 6, 8, NULL, 20, false, false, false, 'https://www.youtube.com/watch?v=GuIreyg-Tcc', 2018, 'Flyway', 0);
INSERT INTO public.board_game (id, title, title_normalized, description, min_players, max_players, min_age, max_age, estimated_play_time, can_play_only_once, is_extension, is_cooperative, tutorial_url, year_published, created_by, version) VALUES (83, 'Vládci Podzemí', 'vladci podzemi', 'Vžite se do kože vládcu podzemia, zvolajte svojich služobníkov, vybudujte sieť chodieb a miestností, nastražte v nich pasce, najímajte netvorov a pokúste sa zabrániť hrdinom, aby vaše dielo dobyli a vyrabovali! Uvidíte, že sa od teraz budete na temné zákutia rôznych temných brlohov a ich obyvateľov pozerať cez úplne inú prizmu!', 2, 4, 12, NULL, 90, false, false, false, 'https://www.youtube.com/watch?v=P7qFLu7swaA', 2017, 'Flyway', 0);
